import { useEffect, useState } from "react";
import axios from "axios";

export default function HomePage() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: "", author: "", year: "" });

  // Fetch all books
  useEffect(() => {
    axios.get("http://localhost:5000/api/books")
      .then(res => setBooks(res.data))
      .catch(err => console.log(err));
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add new book
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/books", form);
      setBooks([...books, res.data]); // add new book to list
      setForm({ title: "", author: "", year: "" }); // reset form
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>ðŸ“š Book Library</h1>

      {/* Add Book Form */}
      <form onSubmit={handleSubmit}>
        <input 
          type="text" name="title" placeholder="Title" 
          value={form.title} onChange={handleChange} required
        />
        <input 
          type="text" name="author" placeholder="Author" 
          value={form.author} onChange={handleChange} required
        />
        <input 
          type="number" name="year" placeholder="Year" 
          value={form.year} onChange={handleChange} required
        />
        <button type="submit">Add Book</button>
      </form>

      {/* Book List */}
      <ul>
        {books.map((book) => (
          <li key={book._id}>
            <b>{book.title}</b> by {book.author} ({book.year})
          </li>
        ))}
      </ul>
    </div>
  );
}
