import React from "react";
import Navbar from "../components/Navbar";
import BookCard from "../components/BookCard";

const dummyBooks = [
  {
    id: 1,
    title: "Rich Dad Poor Dad",
    author: "Robert Kiyosaki",
    coverUrl: "https://m.media-amazon.com/images/I/81bsw6fnUiL.jpg",
  },
  {
    id: 2,
    title: "The Subtle Art of Not Giving a F*ck",
    author: "Mark Manson",
    coverUrl: "https://m.media-amazon.com/images/I/71QKQ9mwV7L.jpg",
  },
  {
    id: 3,
    title: "Atomic Habits",
    author: "James Clear",
    coverUrl: "https://m.media-amazon.com/images/I/91bYsX41DVL.jpg",
  },
];

export default function HomePage() {
  return (
    <div className="bg-orange-50 min-h-screen">
      <Navbar />

      {/* Hero Section */}
      <section className="text-center py-20">
        <h2 className="text-4xl font-bold text-brown-800">
          There is no friend as loyal as a book
        </h2>
        <p className="text-gray-600 mt-4 max-w-xl mx-auto">
          Reading boosts focus, memory, empathy, and communication, reduces stress,
          enhances mental health, and may even help you live longer.
        </p>
        <div className="mt-6 flex justify-center gap-4">
          <button className="bg-orange-500 text-white px-6 py-2 rounded">Shop Now</button>
          <button className="border border-orange-500 text-orange-500 px-6 py-2 rounded">
            Best Selling Books
          </button>
        </div>
      </section>

      {/* Books Showcase */}
      <section className="px-10 py-10 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-6">
        {dummyBooks.map((book) => (
          <BookCard key={book.id} book={book} />
        ))}
      </section>
    </div>
  );
}
